	/** 
	 * Devuelve la consulta estadstica de los Desfibriladores
	 */
	public ArrDesfibrilador[] getEstadisticas(ArrDesfibrilador dai) throws ArrDesfibriladorDaoException{
		StringBuffer sql = new StringBuffer();

		sql.append("SELECT pac.nhc paciente, CODIGO_DESFIBRILADOR, FECHA, ANOSEVO,");
		sql.append(" ISQUEMICA, REVASCPREVIA, INFARTOPREVIO, INFARTOPREVIOANO,");
		sql.append(" DILATNOISQUEM, HIPERTROFICA, DAVD, VALVULAR, OTRACARDIOSTR,");
		sql.append(" BRUGADA, OTRACARDIO2STR, FRACCION, IC, CLASEFUNCIONAL, ENFCONDUCCION,");
		sql.append(" ENFCONDUCCIONSTR, RITMOBASAL, RITMOBASALSTR, ANTECINSRENAL, ANTECFA,");
		sql.append(" DIABETES, HIPERTENSION, ANTECMUERTESUBITA, ANCHURAQRS, FUMADOR,");
		sql.append(" HIPERCOLES, ACV, ARRCLINICA, PRESCLINICA, PRESCLINICASTR, EEF,");
		sql.append(" ARRINDUCIDA, IMPLANTE, FECIMPLANTE, LUGARIMPLANTE, IMPLANTADOR,");
		sql.append(" GENERADOR, FECIMPLPREVIO, RECAMBIO, TVFV, TERAPIA, LOCIMPLANTE,");
		sql.append(" ELECSISACT, FECIMPLANTER, ELECADIC, ELECPREV, INDUC, UMBRAL, NCHOQUES,");
		sql.append(" COMPLNINGUNA, COMPLMUERTE, COMPLTAP, COMPLNEUM, COMPLDISEC, COMPLOTRO,");
		sql.append(" COMPLSITUA, ESTIMANTIBRADI, ALGORITMO, ATP, MODELO, OTRACOMP, NDAI,");
		sql.append(" EPISODIO, AD, VD, VI");
		sql.append(" from arr_desfibrilador dai, arr_pacientes pac");
		sql.append(" where pac.codigo = dai.codigo_paciente");
		sql.append(generaWhere(dai));
		sql.append(" order by 1");
		return findByDynamicSelect(sql.toString());
	}
	
	private String generaWhere(ArrDesfibrilador dai){
		String where = "";
		//Sexo
		Date hoy = new Date();
		if(dai.getAnosevo() != null && dai.getAnosevo() > 0)
			where += " and pac.sexo = " + dai.getAnosevo();
		if(dai.getAcv() != null && dai.getAcv() > 0)
			where += " and pac.fechanaci < '" + UtilFechas.dateToString(new Date(hoy.getYear()-new Integer(dai.getAcv()), hoy.getMonth(), hoy.getDay())) + "'";
		if(dai.getAd() != null && dai.getAd() > 0)
			where += " and pac.fechanaci > '" + UtilFechas.dateToString(new Date(hoy.getYear()-new Integer(dai.getAd()), hoy.getMonth(), hoy.getDay())) + "'";
		
		if(dai.getIsquemicab())
			where += " and dai.isquemica = 1";
		if(dai.getDilatnoisquemb())
			where += " and dai.DILATNOISQUEM = 1";
		if(dai.getFraccion() != null && dai.getFraccion() > 0)
			where += " and dai.fraccion = " + dai.getFraccion();
		String enfconduccion = "111111";
		if(dai.getEnfconduccion1()) enfconduccion.replace(enfconduccion.charAt(0), '2');
		if(dai.getEnfconduccion2()) enfconduccion.replace(enfconduccion.charAt(1), '2');
		if(dai.getEnfconduccion3()) enfconduccion.replace(enfconduccion.charAt(2), '2');
		if(dai.getEnfconduccion4()) enfconduccion.replace(enfconduccion.charAt(3), '2');
		if(dai.getEnfconduccion5()) enfconduccion.replace(enfconduccion.charAt(4), '2');
		if(dai.getEnfconduccion6()) enfconduccion.replace(enfconduccion.charAt(5), '2');
		if(dai.getIc() != null && dai.getIc() > 0)
			where += " and dai.ic = " + dai.getIc();
		if(dai.getAntecfa() != null && dai.getAntecfa() > 0)
			where += " and dai.antecfa = " + dai.getAntecfa();
		if(dai.getArrclinica() != null && dai.getArrclinica() > 0)
			where += " and dai.arrclinica = " + dai.getArrclinica();
		if(dai.getPresclinica() != null && dai.getPresclinica() > 0)
			where += " and dai.presclinica = " + dai.getPresclinica();
		if(dai.getEef() != null && dai.getEef() > 0)
			where += " and dai.eef = " + dai.getEef();
		if(dai.getImplante() != null && dai.getImplante() > 0)
			where += " and dai.implante = " + dai.getImplante();
		if(dai.getImplantador() != null && dai.getImplantador() > 0)
			where += " and dai.implantador = " + dai.getImplantador();
		if(dai.getGenerador() != null && dai.getGenerador() > 0)
			where += " and dai.generador = " + dai.getGenerador();
		if(dai.getGenerador() == 2 && dai.getRecambio() != null && dai.getRecambio() > 0)
			where += " and dai.recambio = " + dai.getRecambio();
		if(dai.getInduc() != null && dai.getInduc() > 0)
			where += " and dai.induc = " + dai.getInduc();
		if(dai.getModelo() != null && dai.getModelo() > 0)
			where += " and dai.modelo = " + dai.getModelo();
		if(dai.getCompldisecb()) where += " and dai.compldisec = 1";
		if(dai.getComplmuerteb()) where += " and dai.complmuerte = 1";
		if(dai.getComplneumb()) where += " and dai.complneum = 1";
		if(dai.getComplningunab()) where += " and dai.complninguna = 1";
		if(dai.getComplotrob()) where += " and dai.complotro = 1";
		if(dai.getComplsituab()) where += " and dai.complsitua = 1";
		if(dai.getCompltapb()) where += " and dai.compltap = 1";
		
		return where;
	}
	
