	/**
	 * Fetches a single row from the result set
	 */
	protected ArrEstudio fetchSingleResult(ResultSet rs) throws SQLException {
		if (rs.next()) {
			ArrEstudio dto = new ArrEstudio();
			populateDto(dto, rs);
			return dto;
		} else {
			return null;
		}

	}

	/**
	 * Fetches multiple rows from the result set
	 */
	protected ArrEstudio[] fetchMultiResults(ResultSet rs) throws SQLException {
		Collection resultList = new ArrayList();
		while (rs.next()) {
			ArrEstudio dto = new ArrEstudio();
			populateDto(dto, rs);
			resultList.add(dto);
		}

		ArrEstudio ret[] = new ArrEstudio[resultList.size()];
		resultList.toArray(ret);
		return ret;
	}

