	public ArrayList<ArrayList<Object>> getEstadisticas(Date fechaini, Date fechafin,ArrEstudio eef, ArrEstudioIntento intento, ArrEstudioAblacion ablacion) throws ArrEstudioDaoException{
		StringBuffer sql = new StringBuffer();
		
		sql.append("select pac.nhc PACIENTE,");
		sql.append("ee.fecha FECHA,");
		sql.append("ee.nestudio NESTUDIO,");
		sql.append("nproc.idproc IDPROC,");
		sql.append("nproc.numproc NUMPROC,");
		sql.append("nproc.idmotivo IDMOTIVO,");
		sql.append("m1.valor DIAGNOSTICO,");
		sql.append("ee.anestesista ANESTESIA,");
		sql.append("ee.navegador NAVEGADOR,");
		sql.append("ee.ecografia ECO,");
		sql.append("ee.escopia ESCOPIA,");
		sql.append("ee.tescopia T_ESCOPIA,");
		sql.append("COUNT(c.idloc) NCATETERES,");
		sql.append("m4.valor TIPO_PROC,");
		sql.append("ee.hllegada TLLEGADA,");
		sql.append("ee.hpuncion TPUNCION,");
		sql.append("ee.hablacion TABLACION,");
		sql.append("ee.hcateter TCATETER,");
		sql.append("ee.hsalida TSALIDA,");
		sql.append("m5.valor RITMO,");
		sql.append("ecg.idqrs QRS,");
		sql.append("ecg.qrs INTERVALO,");
		sql.append("comp.idprocedimiento COMPLICACION,");
		sql.append("comp.idprocedimienton4 MUERTE,");
		sql.append("ee.idestudio, ");
		sql.append("ee.angiografia ANGIOGRAFIA,");
		sql.append("ee.robot ROBOT ");
		
		
		if(eef.getEscopia() == 1){
			sql.append(",i.idnivel1 TIPO");
			sql.append(",i.idnivel2 COMPLICACION");
			sql.append(",i.idnivel3 BLOQUEO");
			sql.append(",i.otros OTRA_COMP");
		}if(eef.getEscopia() == 2){
			sql.append(",ab.sustrato SUSTRATO,");
			sql.append("apl.idvia ABORDAJE,");
			sql.append("cat.tipoablacion ENERGIA,");
			sql.append("apl.tiempo TIEMPO,");
			sql.append("apl.potencia POTENCIA,");
			sql.append("cat.modelo CATETER,");
			sql.append("ab.idnodo SUSTRATO_N1,");
			sql.append("ab.iddcha SUSTRATO_N2,");
			sql.append("res.idres RESULTADO,");
			sql.append("res.idtin EXITO,");
			sql.append("res.idparcial PARCIAL");
			/*sql.append("com.idcomplicacion COMPLICACION,");
			sql.append("com.idmuerte MUERTE,");
			sql.append("com.otros OTRAS_COMP");*/
		}
			
		sql.append(" from arr_pacientes pac,");
		
		if(eef.getEscopia() == 1)
			sql.append(" arr_estudio_intento i,");
		if(eef.getEscopia() == 2){
			sql.append(" arr_estudio_ablacion ab,");
			sql.append(" arr_estudio_resultado res,");
			//sql.append(" arr_ablacion_complica com,");
			sql.append(" arr_estudio_cat_apl apl,");
			sql.append(" arr_estudio_cat_uso uso,");
			sql.append(" cateter cat,");
		}
			
		sql.append(" arr_estudio ee");
		sql.append(" LEFT OUTER JOIN arr_maestras m1 ON ee.diagnostico = m1.codigo");
		sql.append(" LEFT OUTER JOIN arr_maestras m4 ON ee.tipo_procedimiento = m4.codigo");
		sql.append(" LEFT OUTER JOIN arr_estudio_complicaciones comp ON ee.idestudio = comp.idestudio");
		sql.append(" LEFT OUTER JOIN arr_estudio_nproc nproc ON ee.idestudio = nproc.idestudio");
		//sql.append(" LEFT OUTER JOIN arr_estudio_pop po ON ee.idestudio = po.idestudio");
		//sql.append(" LEFT OUTER JOIN arr_estudio_sop so ON ee.idestudio = so.idestudio");
		sql.append(" LEFT OUTER JOIN arr_estudio_ecg ecg ON ee.idestudio = ecg.idestudio");
		sql.append(" LEFT OUTER JOIN arr_maestras m5 ON ecg.idritmo    = m5.codigo");
		sql.append(" LEFT OUTER JOIN arr_enf e ON ee.idestudio = e.id_eef");
		sql.append(" LEFT OUTER JOIN arr_estudio_cateter c ON c.idenfermeria = e.id_registro");
		sql.append(" WHERE pac.codigo = ee.idpaciente");
		sql.append(" and ee.fecha between '"+UtilFechas.getFechaString(fechaini)+"' and '"+UtilFechas.getFechaString(fechafin)+"'");
		sql.append(generaWhere(eef, intento, ablacion));
		sql.append(" GROUP BY pac.nhc, ee.fecha, ee.nestudio, nproc.idproc, nproc.numproc, nproc.idmotivo, m1.valor, ee.anestesista, ee.navegador, ee.ecografia, ee.escopia, ee.tescopia, m4.valor, ee.hllegada, ee.hpuncion, ee.hablacion, ee.hcateter, ee.hsalida, m5.valor, ecg.idqrs, ecg.qrs, comp.idprocedimiento, comp.idprocedimienton4, ee.idestudio, ee.angiografia, ee.robot");
		
		if(eef.getEscopia() == 1)
			sql.append(",i.idnivel1,i.idnivel2,i.idnivel3,i.otros");
		if(eef.getEscopia() == 2)
			sql.append(",apl.idvia,cat.tipoablacion,apl.tiempo,apl.potencia,cat.modelo,ab.idnodo,ab.iddcha,res.idres,res.idtin,res.idparcial,ab.sustrato");
			//sql.append(",ab.idabordaje,ab.idenergia,ab.tcrio,ab.tradio,ab.cateter,ab.idnodo,ab.iddcha,res.idres,res.idtin,res.idparcial,com.idcomplicacion,com.idmuerte,com.otros,ab.sustrato");
		sql.append(" ORDER BY 1");
		
		return findByDynamicSelect(sql.toString());
	}
	
	private String generaWhere(ArrEstudio eef, ArrEstudioIntento intento, ArrEstudioAblacion ablacion){
		String where = "";
		//Filtro de paciente
		Date hoy = new Date();
		if(eef.getEdadmax() != null && eef.getEdadmax().length() > 0)
			where += " and pac.fechanaci > '" + UtilFechas.getFechaString(new Date(hoy.getYear()-new Integer(eef.getEdadmax()), hoy.getMonth(), hoy.getDay())) + "'";
		if(eef.getEdadmin() != null && eef.getEdadmin().length() > 0)
			where += " and pac.fechanaci < '" + UtilFechas.getFechaString(new Date(hoy.getYear()-new Integer(eef.getEdadmin()), hoy.getMonth(), hoy.getDay())) + "'";
		if(eef.getIdpaciente() != null && eef.getIdpaciente() >= 0)
			where += " and pac.sexo = " + eef.getIdpaciente();
		
		//Filtro de estudio
		if(eef.getDiagnostico() != null && eef.getDiagnostico() >= 1)
			where += " and ee.diagnostico = " + eef.getDiagnostico();
		if(eef.getNavegador() != null && eef.getNavegador() >= 0)
			where += " and ee.navegador = " + eef.getNavegador();
		if(eef.getTipoProcedimiento() != null && eef.getTipoProcedimiento() >= 1)
			where += " and ee.tipo_procedimiento = " + eef.getTipoProcedimiento();
		if(eef.getAnestesista() != null && eef.getAnestesista() >= 1)
			where += " and po.idoperador = " + eef.getAnestesista();
		
		//Filtro intento
		if(eef.getEscopia() == 1){
			where += " and i.idestudio = ee.idestudio";
			if(intento != null && intento.getIdnivel1() != null && intento.getIdnivel1() > 0)
				where += " and i.idnivel1 = " + intento.getIdnivel1();
			
		}
		
		//Filtro ablacion
		if(eef.getEscopia() == 2){
			where += " and ab.idestudio = ee.idestudio";
			where += " and res.idestudio = ee.idestudio";
			where += " and res.idablacion = ab.idablacion";
			where += " and apl.idablacion = ab.idablacion";
			where += " and apl.idcatuso = uso.idcatuso";
			where += " and uso.ideef = ee.idestudio";
			where += " and uso.idcateter = cat.idcateter";
			where += " and cat.tipo = 2";
			//where += " and com.idablacion = ab.idablacion";
			//where += " and com.idestudio = ab.idestudio";
			if(ablacion.getIdnodo() != null && ablacion.getIdnodo() >= 0)
				where += " and ab.idnodo = " + ablacion.getIdnodo();
			if(ablacion.getResultadodto() != null && 
					ablacion.getResultadodto().getIdres() != null && 
					ablacion.getResultadodto().getIdres() >= 0)
				where += " and res.idres = " + ablacion.getResultadodto().getIdres();
			/*if(ablacion.getComplicacion() != null && ablacion.getListacomplicacion().size()>0)
				if(ablacion.getListacomplicacion().get(0).getIdlugar() != null &&
						ablacion.getListacomplicacion().get(0).getIdlugar() == 2){
					if(ablacion.getListacomplicacion().get(0).getIdcomplicacion() != null && 
							ablacion.getListacomplicacion().get(0).getIdcomplicacion() >= 0)
						where += " and com.idcomplicacion = " + ablacion.getListacomplicacion().get(0).getIdcomplicacion();
					else
						where += " and com.idcomplicacion > 0 ";
				}else if(ablacion.getListacomplicacion().get(0).getIdlugar() != null &&
						ablacion.getListacomplicacion().get(0).getIdlugar() == 1){
					where += " and (com.idprocedimiento = 0 or com.idcomplicacion is null or com.idcomplicacion = 0) ";
				}else{
					where += " and (com.idprocedimiento > 0 or com.idcomplicacion is null or com.idcomplicacion = 0) ";
				}*/
		}
		// Filtro sin ablacion
		if(eef.getEscopia() == -1){
			where += " and ee.ablacion = 0 ";
		}
		return where;
	}
	
